import React, { useState } from 'react';

import { Button, Space, Upload, message, Card, List, Image, Spin } from 'antd';
import { InboxOutlined } from '@ant-design/icons';
import { ipcRenderer } from 'electron';

import 'antd/dist/antd.css';
import './App.less';

const { Dragger } = Upload;

const App = () => {
    const [ImageBase64, setImageBase64] = useState('');
    const [glossary, setGlossary] = useState([]);
    const [spinning, setSpinning] = useState(false);

    const props = {
        name: 'file',
        multiple: false,
        showUploadList: false,
        beforeUpload() {
            /* 手动上传 */
            return false;
        },
        onChange(e) {
            message.success('上传成功，点击图片可预览');
            const fileReader = new FileReader();
            fileReader.readAsDataURL(e.file);
            fileReader.onload = (file) => {
                setImageBase64(file.target.result);
            };
        },
        onDrop(e) {},
    };

    const EnglishOCR = () => {
        if (!ImageBase64) {
            message.error('请先上传图片！');
            return;
        }
        setSpinning(true);
        ipcRenderer.send('EnglishOCR-message', { ImageBase64 });

        ipcRenderer.on('EnglishOCR-reply', (event, arg) => {
            setGlossary(arg.split('\n'));
            setSpinning(false);
        });
    };

    return (
        <div>
            <Spin spinning={spinning} tip="识别中，请耐心等待...">
                <Space className="ocr-container">
                    <Card title="拖拽上传图片识别英文单词">
                        <Dragger {...props}>
                            <p className="ant-upload-drag-icon">
                                <InboxOutlined />
                            </p>
                            <p className="ant-upload-text">单击或拖动文件到此区域以上传</p>
                            <p className="ant-upload-hint">仅支持单个上传</p>
                        </Dragger>
                        <Button type="primary" onClick={EnglishOCR} block>
                            开始识别英文单词
                        </Button>
                    </Card>
                    <Card className="ocr-card">
                        <Image
                            className="ocr-img"
                            width={200}
                            fallback="data:image/png;base64,"
                            src={`${ImageBase64}`}
                        />
                    </Card>
                </Space>

                <List
                    header={<div>词汇表解析结果</div>}
                    bordered
                    dataSource={glossary}
                    renderItem={(item) => <List.Item>{item}</List.Item>}
                />
            </Spin>
        </div>
    );
};

export default App;
